(function() {
	document.addEventListener('DOMContentLoaded', function() {
		document.querySelectorAll('[data-dc-url]').forEach(e => {
			const dcUrl = e.getAttribute('data-dc-url');
			fetch(dcUrl + '?ajax', { credentials: 'include' })
				.then(r => e.innerHTML = r)
				.catch(e => console.error(e));
		});
	});
})()
